import React from "react";

const Projects = () => {
  const projectList = [
    {
      title: "E-commerce Dashboard",
      description:
        "Dashboard quản lý với React, Chart.js và Redux Toolkit cho hệ thống thương mại điện tử",
      image:
        "https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/1c2ede93-6b81-494d-9cd8-7f0648de35a5.png",
      tags: ["React", "Redux", "Chart.js"],
    },
    {
      title: "Social Media App",
      description:
        "Ứng dụng mạng xã hội với real-time messaging, authentication và responsive design",
      image:
        "https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/47a33565-695b-487a-86f0-79db303754be.png",
      tags: ["React", "Firebase", "Socket.io"],
    },
    {
      title: "Task Management",
      description:
        "Hệ thống quản lý công việc với drag & drop, calendar và thống kê hiệu suất",
      image:
        "https://storage.googleapis.com/workspace-0f70711f-8b4e-4d94-86f1-2a93ccde5887/image/43c98a9e-8ce6-43bc-9f42-913e505b0389.png",
      tags: ["React", "TypeScript", "DnD"],
    },
  ];

  return (
    <section id="projects" className="py-20 bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Tiêu đề */}
        <div className="text-center mb-16 fade-in">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">
            Dự Án Đã Tham gia
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Khám phá những dự án ReactJS mà tôi đã phát triển với đam mê và sự sáng tạo
          </p>
          <div className="w-20 h-1 bg-blue-600 mx-auto mt-4"></div>
        </div>

        {/* Danh sách dự án */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projectList.map((project, index) => (
            <div
              key={index}
              className="bg-white rounded-xl shadow-lg overflow-hidden card-hover fade-in"
            >
              <div className="project-image">
                <img
                  src={project.image}
                  alt={project.title}
                  className="w-full h-48 object-cover"
                />
              </div>
              <div className="p-6">
                <h3 className="text-xl font-semibold text-gray-800 mb-2">
                  {project.title}
                </h3>
                <p className="text-gray-600 mb-4">{project.description}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.tags.map((tag, i) => (
                    <span
                      key={i}
                      className="bg-blue-100 text-blue-700 px-3 py-1 rounded-full text-sm"
                    >
                      {tag}
                    </span>
                  ))}
                </div>
                <a
                  href="#"
                  className="text-blue-600 hover:text-blue-700 font-semibold flex items-center"
                >
                  Xem thêm <i className="fas fa-arrow-right ml-2 text-sm"></i>
                </a>
              </div>
            </div>
          ))}
        </div>

        {/* Nút xem tất cả */}
        <div className="text-center mt-12 fade-in">
          <a
            href="#"
            className="inline-flex items-center text-blue-600 hover:text-blue-700 font-semibold"
          >
            Xem tất cả dự án <i className="fas fa-arrow-right ml-2"></i>
          </a>
        </div>
      </div>
    </section>
  );
};

export default Projects;
