import React, { useState, useEffect } from "react";
import avatar from "../../../Assests/avatar.jpg";
const Hero = () => {
  const fullText = "ReactJS Developer & Frontend Specialist";
  const [displayedText, setDisplayedText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < fullText.length) {
      const timeout = setTimeout(() => {
        setDisplayedText((prev) => prev + fullText.charAt(index));
        setIndex((prev) => prev + 1);
      }, 100);
      return () => clearTimeout(timeout);
    }
  }, [index, fullText]);

  return (
    <section
      id="home"
      className="bg-gradient-to-r from-purple-700 to-blue-600 text-white min-h-screen flex items-center pt-16"
    >
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20 w-full">
        <div className="text-center">
          {/* Avatar */}
          <div className="w-52 h-52 mx-auto mb-8 rounded-full bg-white/20 backdrop-blur-sm p-2">
            <img
              src={avatar}
              alt="Nguyễn Văn A"
              className="w-full h-full rounded-full object-cover"
            />
          </div>

          {/* Tên */}
          <h1 className="text-4xl md:text-6xl font-bold mb-4">Nguyễn Văn A</h1>

          {/* Chức danh với hiệu ứng gõ chữ */}
          <div className="text-xl md:text-2xl text-blue-100 mb-8 font-semibold">
            {displayedText}
            <span className="text-blue-400 animate-blink">|</span>
          </div>

          {/* Mô tả */}
          <p className="text-lg md:text-xl mb-8 max-w-2xl mx-auto">
            Tôi chuyên phát triển ứng dụng web hiện đại với ReactJS, tạo ra trải
            nghiệm người dùng tuyệt vời và giao diện đẹp mắt.
          </p>

          {/* CTA Buttons */}
          <div className="flex justify-center space-x-4">
            <a
              href="#projects"
              className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-3 rounded-lg font-semibold transition duration-300"
            >
              Xem Dự Án
            </a>
            <a
              href="#contact"
              className="border-2 border-white text-white hover:bg-white hover:text-blue-600 px-8 py-3 rounded-lg font-semibold transition duration-300"
            >
              Liên Hệ
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
