import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import styles from "../../styles/styles";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import { categoriesData, productData } from "../../static/data";
import {
  AiOutlineHeart,
  AiOutlineSearch,
  AiOutlineShoppingCart,
} from "react-icons/ai";
import { IoIosArrowDown, IoIosArrowForward } from "react-icons/io";
import { BiMenuAltLeft } from "react-icons/bi";
import { CgProfile } from "react-icons/cg";
import DropDown from "./DropDown";
import Navbar from "./Navbar";
import { useSelector } from "react-redux";
import Cart from "../cart/Cart";
import Wishlist from "../Wishlist/Wishlist";
import { FaArrowAltCircleDown } from "react-icons/fa";
import { Button, Typography } from "@material-ui/core";

const Header = ({ activeHeading }) => {


  const { allProducts } = useSelector((state) => state.products);

  const [active, setActive] = useState(false);


  const searchBoxRef = useRef(); // reference to the search box

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        searchBoxRef.current &&
        !searchBoxRef.current.contains(event.target)
      ) {
        setSearchData(null); // clear search data if click was outside the search box
      }
    };
    // add the event listener
    document.addEventListener("mousedown", handleClickOutside);

    // remove the event listener when the component unmounts
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleSearchChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);

    const filteredProducts =
      allProducts &&
      allProducts.filter((product) =>
        product.name.toLowerCase().includes(term.toLowerCase())
      );
    setSearchData(filteredProducts);
    setShowAllResults(false); // Reset to show limited results initially
  };

  window.addEventListener("scroll", () => {
    if (window.scrollY > 70) {
      setActive(true);
    } else {
      setActive(false);
    }
  });
  const handleShowAllResults = () => {
    setShowAllResults(true);
  };

  return (
    <>
      <div
        className={`${
          active ? "shadow-sm fixed top-0 left-0 z-10" : ""
        } transition hidden 800px:flex flex-row items-center space-around w-full bg-[#f0f9ff] h-[100px]`}
      >
        <div className="text-3xl font-bold text-blue-600 mb-2">Portfolio</div>
        <div className={`${styles.noramlFlex}`}>
          <Navbar active={activeHeading} />
        </div>
      </div>
    </>
  );
};

export default Header;
