import React from "react";

const Skills = () => {
  const devSkills = [
    {
      title: "ReactJS",
      icon: "fab fa-react text-primary-600",
      color: "bg-primary-100",
      description: "Hooks, Context API, Redux, Performance Optimization",
    },
    {
      title: "JavaScript",
      icon: "fab fa-js-square text-yellow-500",
      color: "bg-yellow-100",
      description: "ES6+, Modern Syntax, Async Programming",
    },
    {
      title: "Node.js",
      icon: "fab fa-node-js text-green-500",
      color: "bg-green-100",
      description: "REST APIs, Express, Database Integration",
    }, {
      title: "Node.js",
      icon: "fab fa-node-js text-green-500",
      color: "bg-green-100",
      description: "REST APIs, Express, Database Integration",
    },
  ];

  const designSkills = [
    {
      title: "Illustrator",
      icon: "fas fa-image text-red-500",
      color: "bg-red-100",
      description: "Photo Editing, Retouching, Graphic Design",
    },
    {
      title: "Figma",
      icon: "fab fa-figma text-purple-500",
      color: "bg-purple-100",
      description: "Wireframes, Prototypes, Component Systems",
    },
    {
      title: "Photoshop",
      icon: "fas fa-image text-red-500",
      color: "bg-red-100",
      description: "Photo Editing, Retouching, Graphic Design",
    },
  ];

  const mediaSkills = [
    {
      title: "Illustrator",
      icon: "fas fa-pen-nib text-orange-500",
      color: "bg-orange-100",
      description: "Vector Design, Branding, Typography",
    },
    {
      title: "Premiere Pro",
      icon: "fas fa-video text-indigo-500",
      color: "bg-indigo-100",
      description: "Video Editing, Transitions, Sound Sync",
    },
    {
      title: "Lightroom",
      icon: "fas fa-camera text-teal-500",
      color: "bg-teal-100",
      description: "Photo Color Grading, Exposure, Composition",
    },
     {
      title: "Lightroom",
      icon: "fas fa-camera text-teal-500",
      color: "bg-teal-100",
      description: "Photo Color Grading, Exposure, Composition",
    },
  ];

  const renderSkillGroup = (title, skills) => (
    <div className="mb-16">
      <h3 className="text-2xl font-bold text-gray-800 mb-6">{title}</h3>
      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
        {skills.map((skill, index) => (
          <div
            key={index}
            className="text-center p-6 bg-gray-50 rounded-lg shadow fade-in"
          >
            <div
              className={`w-16 h-16 ${skill.color} rounded-full flex items-center justify-center mx-auto mb-4`}
            >
              <i className={`${skill.icon} text-3xl`}></i>
            </div>
            <h4 className="text-xl font-semibold mb-2">{skill.title}</h4>
            <p className="text-gray-600">{skill.description}</p>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <section id="skills" className="py-20 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Tiêu đề */}
        <div className="text-center mb-16 fade-in">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">
            Kỹ Năng Chuyên Môn
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Các công nghệ và công cụ mà tôi thành thạo trong phát triển ứng dụng web, thiết kế và media
          </p>
          <div className="w-20 h-1 bg-blue-600 mx-auto mt-4"></div>
        </div>

        {/* Các nhóm kỹ năng */}
        {renderSkillGroup("Phát triển ứng dụng", devSkills)}
        {renderSkillGroup("Thiết kế giao diện", designSkills)}
        {renderSkillGroup("Media & sáng tạo", mediaSkills)}
      </div>
    </section>
  );
};

export default Skills;
