import React from "react";
import { Link } from "react-router-dom";
import Button from "@mui/material/Button";
import { Box, Typography, Avatar } from "@mui/material";

const Hero = () => {
  return (
    <Box
      sx={{
        minHeight: "80vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "linear-gradient(to right, #7e57c2, #1976d2)",
        color: "#fff",
        textAlign: "center",
        px: 2,
      }}
    >
      <Box>
        {/* Ảnh đại diện */}
        <Avatar
          src="https://res.cloudinary.com/djxsh5hhw/image/upload/v1703686408/20773_yngarp.jpg"
          alt="Nguyễn Văn A"
          sx={{
            width: 140,
            height: 140,
            mx: "auto",
            mb: 3,
            boxShadow: 3,
          }}
        />

        {/* Tên và chức danh */}
        <Typography variant="h4" fontWeight="bold">
          Nguyễn Văn A
        </Typography>
        <Typography variant="h6" sx={{ mb: 2 }}>
          ReactJS Developer & Frontend Specialist
        </Typography>

        {/* Mô tả ngắn */}
        <Typography variant="body1" sx={{ maxWidth: 600, mx: "auto", mb: 4 }}>
          Tôi chuyên phát triển ứng dụng web hiện đại với ReactJS, tạo ra trải nghiệm người dùng tuyệt vời và giao diện đẹp mắt.
        </Typography>

        {/* Nút CTA */}
        <Box sx={{ display: "flex", justifyContent: "center", gap: 2 }}>
          <Link to="/projects">
            <Button variant="contained" color="info">
              Xem Dự Án
            </Button>
          </Link>
          <Link to="/contact">
            <Button variant="outlined" sx={{ color: "#fff", borderColor: "#fff" }}>
              Liên Hệ
            </Button>
          </Link>
        </Box>
      </Box>
    </Box>
  );
};

export default Hero;
