import React from "react";
import { useSelector } from "react-redux";
import styles from "../../../styles/styles";
import ProductCard from "../ProductCard/ProductCard";

const FeaturedProduct = () => {
  const { allProducts } = useSelector((state) => state.products); // Get all products from redux store

  return (
    <div>
      <div className={`${styles.section}`}>
        <div className={`${styles.heading}`}>
          <h1>Sản phẩm mới</h1>
        </div>
        <div className="grid grid-cols-1 gap-[20px] md:grid-cols-2 md:gap-[25px] lg:grid-cols-4 lg:gap-[25px] xl:grid-cols-5 xl:gap-[30px] mb-12 border-0">
          {allProducts &&
            allProducts.length !== 0 && ( // Check if allProducts is not empty
              <>
                {allProducts && // Map through allProducts and display each product
                  allProducts.map(
                    (
                      i,
                      index // Map through allProducts and display each product
                    ) => <ProductCard data={i} key={index} />
                  )}
              </>
            )}
        </div>
      </div>
    </div>
  );
};

export default FeaturedProduct;
